status = -1;

function action(mode, type, selection) {
    if (mode == 1) {
        status++;
    } else {
        if (status == 0) {
            cm.sendOk("不想使用吗？…百宝箱里有各类#b奇特座椅或卷轴、装备、新奇道具#k哦！\r\n\r\n");
            cm.dispose();
        }
        status--;
    }
    if (status == 0) {
		 
		 if(!开放){
			cm.sendOk("抱歉，维护中，暂不开放！\r\n\r\n");
            cm.dispose();
			return;
		 }	
		 var   text = "#r使用";
		 if(消耗道具[0] == 0){
			 text += "#b 金币 #r";
		 }else if(消耗道具[0] == 1){
			 text += "#b 点券 #r";
		 }else if(消耗道具[0] == 2){
			 text += "#b 抵用券 #r";
		 }else{
			 text += "#b #v" + 消耗道具[0] +  "##z" + 消耗道具[0] + "# #r";
		 }
        text += "就可以随机获得百宝箱里的骑宠技能，快行动吧！\r\n#e#d" + 小烟花 + "任何建议，请联系管理，谢谢！" + 小烟花 + "#n#k\r\n\r\n";
		if(消耗道具[0] == 0){
			 text += "#L1#" + 小烟花 +  "使用#b" + 消耗道具[1] + "#k金币抽#r1#k次#l#L10"+"#" + 小烟花 + "使用#b" + 消耗道具[1] * 10 + "#k金币抽#r10#k次\r\n\r\n";
		 }else if(消耗道具[0] == 1){
			 text += "#L1#" + 小烟花 +  "使用#b" + 消耗道具[1] + "#k点券抽#r1#k次#l#L10"+"#" + 小烟花 + "使用#b" + 消耗道具[1] * 10 + "#k点券抽#r10#k次\r\n\r\n";
		 }else if(消耗道具[0] == 2){
			 text += "#L1#" + 小烟花 +  "使用#b" + 消耗道具[1] + "#k抵用券抽#r1#k次#l#L10"+"#" + 小烟花 + "使用#b" + 消耗道具[1] * 10 + "#k抵用券抽#r10#k次\r\n\r\n";
		 }else{
			 text += "#L1#" + 小烟花 +  "使用#v" + 消耗道具[0] + "#x#b" + 消耗道具[1] + "#k抽#r1#k次#l#L10"+"#" + 小烟花 + "使用#v" + 消耗道具[0] + "#x#b" + 消耗道具[1] * 10 + "抵用券抽#r10#k次\r\n\r\n";
		 }
		 
        text += "#L101##r" + 星星 + "想看百宝箱里有什么东西吗" + 星星 + "#l\r\n\r\n";
		
		if(cm.getPlayer().getGMLevel() >= 100){
			text += "\r\n";
			text += "#b---------------------以下为GM可见---------------------\r\n";
			text += "#L103##r" + 星星 + "批量添加抽奖物品" + 星星 + "#l\r\n\r\n";
		}
        cm.sendSimple(text);
    } else if (status == 1) {
        if (selection == 102) {
            cm.dispose();
            cm.openNpc(9310022, 102);
        } else if (selection == 103) {
			if(cm.批量添加抽奖物品(抽奖系列, items, weight, announcement)){
				cm.sendOk("添加成功\r\n");
				cm.dispose();
				return;
			}
            cm.sendOk("添加失败\r\n");
			cm.dispose();
			return;
            status = -1;
            //cm.safeDispose();
        } else if(selection == 106){
			cm.dispose();
			cm.openNpc(9900001, "百宝券抽奖");
			//cm.openNpc(9310022,103 );
			return;
		}else if(selection == 104){
			cm.dispose();
			cm.openNpc(9310022, "装备积分兑换");
			return;
		}else if(selection == 105){
			cm.dispose();
			cm.openNpc(9310022, 104);
			return;
		} else if (selection == 101) {
            var textItem = ""
            // for (var i = 0; i < itemList.length; i++) {
                // textItem += "#i" + itemList[i][0] + ":#"
            // }
			var rewardIDs = cm.读取奖品ID(抽奖系列);
			var chances = cm.读取奖品概率(抽奖系列);
			for(var i = 0; i < rewardIDs.size(); i++){
				textItem += "#i" + rewardIDs.get(i) + ":# ";
			}
			textItem += "\r\n";
            cm.sendOk("奖池中的道具如下：#n#k\r\n" + textItem);
            status = -1;
            //cm.safeDispose();
        } else if (selection > 0 && selection <= 10) {
			//cm.getPlayer().getCSPoints(1) < 500
			
			
			var message = "你获得了:\r\n";
			for(var i = 1;  i <= selection; i++){
				if(消耗道具[0] == 0){
					if(cm.getPlayer().getMeso() < 消耗道具[1]){
						cm.sendOk("金币不足！");
						cm.dispose();
						return;
					}
				}else if(消耗道具[0] == 1){
					if(cm.getPlayer().getCSPoints(1) < 消耗道具[1]){
						cm.sendOk("点券不足！");
						cm.dispose();
						return;
					}
				}else if(消耗道具[0] == 2){
					if(cm.getPlayer().getCSPoints(2) < 消耗道具[1]){
						cm.sendOk("抵用券不足！");
						cm.dispose();
						return;
					}
				}else{
					if(!cm.haveItem(消耗道具[0], 消耗道具[1])){
						cm.sendOk("#v" + 消耗道具[0] + "#不足！");
						cm.dispose();
						return;
					}
				}
				
				if(cm.getPlayer().getInventory(Packages.client.inventory.MapleInventoryType.getByType(1)).isFull() || cm.getPlayer().getInventory(Packages.client.inventory.MapleInventoryType.getByType(2)).isFull() || cm.getPlayer().getInventory(Packages.client.inventory.MapleInventoryType.getByType(3)).isFull() || cm.getPlayer().getInventory(Packages.client.inventory.MapleInventoryType.getByType(4)).isFull() || cm.getPlayer().getInventory(Packages.client.inventory.MapleInventoryType.getByType(5)).isFull()){
					cm.sendOk("请保证你背包的所有类别窗口均有一格以上的空间。！");
					cm.dispose();
					return;
				}
				// var today = new Date();
				// var time = today.getTime();
				
				var itemid = cm.抽奖(抽奖系列, "骑宠技能百宝箱");
				var itemName = Packages.server.MapleItemInformationProvider.getInstance().getName(itemid);
				var item = cm.getNewEquip(itemid);
				if (itemid != -1) {
					//cm.playerMessage(6, "itemid:" + itemid);//测试
					
					if(消耗道具[0] == 0){
						cm.getPlayer().gainMeso(-消耗道具[1], true, false, true);
					}else if(消耗道具[0] == 1){
						cm.getPlayer().modifyCSPoints(1, -消耗道具[1], true);
					}else if(消耗道具[0] == 2){
						cm.getPlayer().modifyCSPoints(1, -消耗道具[1], true);
					}else{
						cm.gainItem(消耗道具[0], -消耗道具[1]);
					}
					//cm.gainItem(itemid, 1);
					cm.getPlayer().addMount(itemid);
					
					message += "#v" + itemid + "#" + " ";
					
					cm.getPlayer().dropMessage(5, "你获得了 " + itemName + "x 1");
					Packages.handling.world.World.Broadcast.broadcastMessage(Packages.tools.MaplePacketCreator.getGachaponMega("[百宝箱]" , " : " + "恭喜 " + cm.getPlayer().getName() + " 从百宝箱中抽到了 " + itemName + "。", item, 1, cm.getPlayer().getClient().getChannel()));
				} else {
					cm.sendOk("请你确认在背包的装备，消耗，其他窗口中是否有一格以上的空间。");
					cm.dispose();
					break;
				}
				// var today1 = new Date();
				// cm.playerMessage(6, "time:" + (today1.getTime() - time));//测试
			}
			message += "\r\n\r\n#r请在《拍卖》→《个人中心》→《切换骑宠技能》处进行设置。#k";
			cm.sendOk(message);
			cm.dispose();
			
        }

    }
}

function 抽奖(){
	var random = Math.random() * (奖池列表.length);
	//cm.playerMessage(6, "random:" + random);//测试
	//cm.playerMessage(6, "parseInt(random):" + parseInt(random));//测试
	return 奖池列表[parseInt(random)];
}

